import pandas as pd
import faiss
import datetime
import numpy as np
from sklearn.feature_extraction.text import TfidfVectorizer
from sklearn.metrics.pairwise import cosine_similarity

# Función para cargar las preguntas y respuestas del archivo CSV
def cargar_preguntas_respuestas(ruta_csv):
    return pd.read_csv(ruta_csv)

# Función para guardar una nueva pregunta en el archivo CSV
def guardar_pregunta(ruta_csv, pregunta, respuesta):
    df = pd.read_csv(ruta_csv)
    nuevo_df = pd.DataFrame({'Pregunta': [pregunta], 'Respuesta': [respuesta]})
    df = pd.concat([df, nuevo_df], ignore_index=True)
    df.to_csv(ruta_csv, index=False)

# Función para generar embeddings utilizando TF-IDF y crear la base vectorial
def crear_base_vectorial(df):
    vectorizer = TfidfVectorizer()
    preguntas_embeddings = vectorizer.fit_transform(df['Pregunta'])
    
    dimension = preguntas_embeddings.shape[1]
    index = faiss.IndexFlatL2(dimension)
    preguntas_embeddings = preguntas_embeddings.toarray().astype('float32')
    index.add(preguntas_embeddings)
    
    faiss.write_index(index, 'faiss_index.index')
    return index, vectorizer

