# Función para responder preguntas usando el índice y el modelo TF-IDF
def responder_pregunta(pregunta, index, vectorizer, df):
    pregunta_embedding = vectorizer.transform([pregunta]).toarray().astype('float32')
    
    _, I = index.search(pregunta_embedding, 1)
    indice_pregunta_mas_cercana = I[0][0]
    respuesta = df.iloc[indice_pregunta_mas_cercana]['Respuesta']
    return respuesta

# Función para iniciar el chatbot
def iniciar_chatbot():
    csv_path = 'Preguntas.csv'
    df = cargar_preguntas_respuestas(csv_path)
    index, vectorizer = crear_base_vectorial(df)

    while True:
        pregunta = input("Ingrese su pregunta (o 'salir' para terminar): ").strip()
        if pregunta.lower() == 'salir':
            break
        
        nivel, respuesta_predefinida = clasificar_pregunta(pregunta)
        
        if respuesta_predefinida:
            print(f"Respuesta: {respuesta_predefinida}")
        else:
            respuesta = responder_pregunta(pregunta, index, vectorizer, df)
            print(f"Respuesta: {respuesta}")

if __name__ == "__main__":
    iniciar_chatbot()
